//@flow
import chai from 'chai';
import chaiAsPromised from "chai-as-promised";
import sinonChai from "sinon-chai";
import articleize from './articleize';

const expect = chai.expect;
chai.use(sinonChai);
chai.use(chaiAsPromised);

describe('articleize', function() {


  it('should add "a" before words not starting with a vowel', function() {
    const word = 'word';

    const articledWord = articleize(word);

    expect(articledWord).to.equal('a word');
  });

  it('should add "an" before words starting with a vowel', function() {
    const word = 'out';

    const articledWord = articleize(word);

    expect(articledWord).to.equal('an out');
  });

});
