// @flow

export default function dasherize(input: string): string {
  if (!input) {
    return "";
  }

  return input
    .replace(/^([A-Z])/, (matches) => matches[0].toLowerCase())
    .replace(/([a-z])([A-Z])/g, (matches) => `${matches[0]}-${matches[1].toLowerCase()}`)
    .replace(/_/g, '-');
}
